int numbers[] = {23, 45, 12, 67, 34};
int size = sizeof(numbers) / sizeof(numbers[0]);
int target = 67;
int index = -1;

for (int i = 0; i < size; i++) {
    if (numbers[i] == target) {
        index = i;
        break;
    }
}

if (index != -1) {
    printf("找到 %d 於索引 %d\n", target, index);
} else {
    printf("未找到 %d\n", target);
}
