#include <stdio.h>

int main() {
    FILE *file = fopen("text.txt", "r");
    if (file == NULL) {
        printf("無法開啟檔案\n");
        return 1;
    }

    char line[256];
    int lineNum = 1;
    while (fgets(line, 256, file) != NULL) {
        printf("%d: %s", lineNum++, line);
    }

    fclose(file);
    return 0;
}
