#include <stdio.h>

typedef struct {
    int id;
    char name[50];
    double salary;
} Employee;

int main() {
    FILE *file = fopen("employees.dat", "wb");
    if (file == NULL) {
        printf("無法建立檔案\n");
        return 1;
    }

    Employee emp = {1001, "John Doe", 50000.0};
    fwrite(&emp, sizeof(Employee), 1, file);

    fclose(file);
    printf("已寫入二進位資料\n");
    return 0;
}
