#include <stdio.h>

typedef struct {
    int id;
    char name[50];
    double salary;
} Employee;

int main() {
    FILE *file = fopen("employees.dat", "rb");
    if (file == NULL) {
        printf("無法開啟檔案\n");
        return 1;
    }

    Employee emp;
    while (fread(&emp, sizeof(Employee), 1, file) == 1) {
        printf("ID: %d, 姓名: %s, 薪資: %.2f\n", 
               emp.id, emp.name, emp.salary);
    }

    fclose(file);
    return 0;
}
