#include <stdio.h>
#include <stdlib.h>

int main() {
    int n = 5;
    int *arr = (int*)calloc(n, sizeof(int));
    if (arr == NULL) {
        printf("記憶體配置失敗\n");
        return 1;
    }

    printf("calloc 已將記憶體初始化為 0:\n");
    for (int i = 0; i < n; i++) {
        printf("arr[%d] = %d\n", i, arr[i]);
    }

    free(arr);
    return 0;
}
