#include <stdio.h>
#include <stdlib.h>

int main() {
    int *arr = (int*)malloc(3 * sizeof(int));
    arr[0] = 10; arr[1] = 20; arr[2] = 30;

    printf("原始陣列 (3 個元素):\n");
    for (int i = 0; i < 3; i++) printf("%d ", arr[i]);
    printf("\n");

    int *temp = (int*)realloc(arr, 5 * sizeof(int));
    if (temp == NULL) {
        free(arr);
        return 1;
    }
    arr = temp;
    arr[3] = 40; arr[4] = 50;

    printf("擴展後 (5 個元素):\n");
    for (int i = 0; i < 5; i++) printf("%d ", arr[i]);
    printf("\n");

    free(arr);
    return 0;
}
