#define MAX(a, b) ((a) > (b) ? (a) : (b))
#define MIN(a, b) ((a) < (b) ? (a) : (b))
#define SQUARE(x) ((x) * (x))
#define ABS(x) ((x) < 0 ? -(x) : (x))

int main() {
    int x = 10, y = 20;
    printf("Max: %d\n", MAX(x, y));
    printf("Min: %d\n", MIN(x, y));
    printf("Square: %d\n", SQUARE(5));
    printf("Abs: %d\n", ABS(-15));
    return 0;
}
