enum Weekday {
    MONDAY,      // 0
    TUESDAY,     // 1
    WEDNESDAY,   // 2
    THURSDAY,    // 3
    FRIDAY,      // 4
    SATURDAY,    // 5
    SUNDAY       // 6
};

enum Color {
    RED = 1,
    GREEN = 2,
    BLUE = 4
};

int main() {
    enum Weekday today = FRIDAY;
    enum Color favorite = BLUE;

    if (today == FRIDAY) {
        printf("快到週末了！\n");
    }

    printf("顏色代碼: %d\n", favorite);
    return 0;
}
