#include <stdio.h>

int main() {
    int num = 42;
    int *ptr = &num;  // 指標指向 num 的地址

    printf("變數值: %d\n", num);
    printf("變數地址: %p\n", (void*)&num);
    printf("指標值 (地址): %p\n", (void*)ptr);
    printf("指標指向的值: %d\n", *ptr);

    // 透過指標修改值
    *ptr = 100;
    printf("修改後的變數值: %d\n", num);

    return 0;
}
