// JavaScript 變數宣告範例

// 使用 let 宣告變數
let age = 25;
let height = 175.5;
let name = "張三";
let isStudent = true;

// 使用 const 宣告常數
const PI = 3.14159;

// 輸出變數值
console.log(`姓名: ${name}`);
console.log(`年齡: ${age}`);
console.log(`身高: ${height} cm`);
console.log(`是否為學生: ${isStudent}`);
console.log(`圓周率: ${PI}`);
