class Student {
    constructor(name, age) {
        this.name = name;
        this.age = age;
        this.grades = [];
    }

    addGrade(grade) {
        this.grades.push(grade);
    }

    getAverage() {
        if (this.grades.length === 0) return 0;
        const sum = this.grades.reduce((total, grade) => total + grade, 0);
        return sum / this.grades.length;
    }

    introduce() {
        return `我是 ${this.name}，今年 ${this.age} 歲`;
    }

    // 靜態方法
    static compareStudents(student1, student2) {
        return student1.getAverage() - student2.getAverage();
    }
}

// 使用類別
const student1 = new Student("小明", 18);
student1.addGrade(85);
student1.addGrade(92);
student1.addGrade(78);

console.log(student1.introduce());
console.log(`平均成績: ${student1.getAverage().toFixed(1)}`);

// 繼承
class HighSchoolStudent extends Student {
    constructor(name, age, year) {
        super(name, age);
        this.year = year;
    }

    introduce() {
        return `${super.introduce()}，我是高中${this.year}年級學生`;
    }
}

const hsStudent = new HighSchoolStudent("小華", 17, 2);
