// Promise 範例
function fetchData(delay) {
    return new Promise((resolve, reject) => {
        setTimeout(() => {
            if (delay < 3000) {
                resolve(`資料載入成功！延遲 ${delay}ms`);
            } else {
                reject('載入超時！');
            }
        }, delay);
    });
}

// 使用 Promise
fetchData(1000)
    .then(result => {
        console.log(result);
        return fetchData(2000);
    })
    .then(result => {
        console.log(result);
    })
    .catch(error => {
        console.error(`錯誤: ${error}`);
    });

// 使用 async/await
async function loadAllData() {
    try {
        console.log('開始載入資料...');

        const result1 = await fetchData(500);
        console.log(result1);

        const result2 = await fetchData(1000);
        console.log(result2);

        console.log('所有資料載入完成！');
    } catch (error) {
        console.error(`載入失敗: ${error}`);
    }
}

// 呼叫非同步函數
loadAllData();
